function renderProjects(type) {
  const { title, caption, projects } = projectData[type];

  // Update heading & caption
  // document.getElementById("project-title").innerHTML = title;
  // document.getElementById("project-caption").innerHTML = caption;

  // Populate project cards
  const container = document.getElementById("projects-container");
  container.innerHTML = "";
  projects.forEach((project) => {
    container.appendChild(createProjectCard(project));
  });
}

function initializeTabs() {
  const buttons = document.querySelectorAll(".tab-btn");

  buttons.forEach((btn) =>
    btn.addEventListener("click", () => {
      // Update active tab style
      buttons.forEach((b) => b.classList.remove("active"));
      btn.classList.add("active");

      const type = btn.getAttribute("data-type");
      renderProjects(type);
    })
  );

  renderProjects("latest"); // default tab
}

document.addEventListener("DOMContentLoaded", initializeTabs);

