function setSticky() {
  if (window.innerWidth <= 991) {
    nav.classList.add("sticky-top");
  } else if (window.innerWidth > 991) {
    nav.classList.remove("sticky-top");
  }
  window.onresize = function () {
    if (window.innerWidth <= 991) {
      nav.classList.add("sticky-top");
    } else if (window.innerWidth > 991) {
      nav.classList.remove("sticky-top");
    }
  };
}

